#include <limits> // For INT_MAX
#include <utility> // For lower bound

template <typename T>
PriorityQueue<T> kthSmallestElement(int k, const std::vector<T>& listA, const std::vector<T>& listB)
{
    // Sort both lists
    sort(listA.begin(), listA.end());
    sort(listB.begin(), listB.end());

    auto itA = std::lower_bound(listA.begin(), listA.end(), 0);
    auto itB = std::lower_bound(listB.begin(), listB.end(), 0);

    // Binary search in the first sorted list
    for (int i = 1; itA != listA.end(); i++, itA = std::lower_bound(itA, listA.end(), i))
    {
        int minIndexA = itA - listA.begin();
        int minValueA = listA[minIndexA];

        // Insert the current element into the priority queue
        PartitionRng<T>::push(minValueA, k, minIndexA, k, listA);

        // Compare with the second sorted list
        PartitionRng<T>::push(minValueA, k+1, minIndexA, k, listA);

        if (!PartitionRng<T>::head())
            goto end;

        // Binary search in the second sorted list
        for (; itB != listB.end(); i++, itB = std::lower_bound(itB, listB.end(), i))
        {
            int minIndexB = itB - listB.begin();
            int minValueB = listB[minIndexB];

            // Insert the current element into the priority queue
            PartitionRng<T>::push(minValueB, k+1, minIndexB, k+1, listB);

            if (!PartitionRng<T>::head())
                goto end;

            // Conflict! We've found an element smaller than k from both lists
            return PartitionRng<T>::queue;
        }
    }

